mapper.select:
		select * from room
			
				host_no = #{host_no}
				
					
						${stype} like '%${sword}%'
					
					
					
			   			 content like '%${sword}%' or title like '%${sword}%'
			    	
				
			
		order by room_no desc
		limit ${startIdx}, ${pageRow}

mapper.update:
UPDATE hotel SET
state_code = #{state_code}, district_code = #{district_code}, hotel_name = #{hotel_name}, addr = #{addr}, tel = #{tel}, hotel_content = #{hotel_content}
WHERE hotel_no = #{hotel_no}

mapper.select:
select * from image 

	
		room_no = #{room_no}
	
	
		hotel_no = #{hotel_no}
	

order by image_order
