mapper.insert:
INSERT INTO guestboard (
	guest_no, gboard_type, gboard_title, gboard_content, gboard_viewcount, gboard_writer, gboard_regdate, gboard_status, filename_org, filename_real
) VALUES (
	#{guest_no}, #{gboard_type}, #{gboard_title}, #{gboard_content}, #{gboard_viewcount}, #{gboard_writer}, now(), #{gboard_status}, #{filename_org}, #{filename_real}
)  

	SELECT LAST_INSERT_ID()

mapper.update:
UPDATE guestboard SET 
	gboard_title = #{gboard_title}, gboard_content = #{gboard_content}, gboard_updatedate = now()
	
	, filename_org = #{filename_org}, filename_real = #{filename_real}
	
	WHERE gboard_no = #{gboard_no}
