mapper.insert:
INSERT INTO guestnotice (
	admin_no, gnotice_title, gnotice_content, gnotice_regdate, gnotice_viewcount
) VALUES (
	#{admin_no}, #{gnotice_title}, #{gnotice_content}, now(), #{gnotice_viewcount}
)

mapper.update:
UPDATE guestnotice SET 
	gnotice_title = #{gnotice_title}, gnotice_content = #{gnotice_content}
	WHERE gnotice_no = #{gnotice_no}
