mapper.insert:
INSERT INTO hostnotice (
	admin_no, hnotice_type, hnotice_title, hnotice_content, hnotice_regdate, hnotice_viewcount, fix
) VALUES (
	#{admin_no}, #{hnotice_type}, #{hnotice_title}, #{hnotice_content}, now(), #{hnotice_viewcount}, #{fix}
)

mapper.select:
SELECT * FROM (
	SELECT row_number() OVER (ORDER BY fix DESC, hnotice_regdate DESC) AS rownum, hnotice_type, hnotice_no, hnotice_title FROM hostnotice
	) AS now 
	WHERE hnotice_no = #{hnotice_no};

SELECT * FROM (
	SELECT row_number() OVER (ORDER BY fix DESC, hnotice_regdate DESC) AS rownum, hnotice_type, hnotice_no, hnotice_title FROM hostnotice
	) AS prev 
	WHERE rownum = #{rownum}-1;

SELECT * FROM (
	SELECT row_number() OVER (ORDER BY fix DESC, hnotice_regdate DESC) AS rownum, hnotice_type, hnotice_no, hnotice_title FROM hostnotice
	) AS next 
	WHERE rownum = #{rownum}+1;

mapper.update:
UPDATE hostnotice SET 
	hnotice_type = #{hnotice_type}, hnotice_title = #{hnotice_title}, hnotice_content = #{hnotice_content}
	WHERE hnotice_no = #{hnotice_no}
