mapper.update:
update guest set totalpoint = 
	(select totalpoint from (select totalpoint from guest where guest_no =#{guest_no})as b)+
	(select used_point from reserv  where reserv_no =#{reserv_no}) where guest_no=#{guest_no};

mapper.select:
select * , (select hotel_name from hotel where hotel_no = reserv.hotel_no) as hotel_name, (select number from room where room_no=reserv.room_no)as number from reserv
guest_no = #{guest_no}
${stype} like '%${sword}%'
content like '%${sword}%' or title like '%${sword}%'
order by reserv_no desc limit ${startIdx}, ${pageRow}
